mapper.select:
/*com.ottt.ottt.dao.CommunityMapper.getArticleList 호출*/ SELECT DISTINCT ta.article_no -- 게시물번호 , tc.article_no as comment_article_no -- 코맨드 작성한 게시물번호 , tal.article_no as like_article_no -- 좋아요 누른 게시물번호 , (SELECT COUNT(*) FROM tb_comment tc2 WHERE tc2.article_no = ta.article_no) as comment_count -- 코멘트 개수 , (SELECT COUNT(*) FROM tb_article_like tal2 WHERE tal2.article_no = ta.article_no) AS like_count -- 좋아요 개수 , tu.user_no -- 유저번호 , ta.article_index_no -- 게시판구분 , ta.article_content -- 게시판 내용 , ta.article_image -- 사진 경로 , ta.article_title -- 게시판 제목 , to_char(ta.article_create_dt,'YYYY-MM-DD') AS article_create_dt -- 등록일 , to_char(ta.article_mod_dt,'YYYY-MM-DD') AS article_create_dt -- 수정일 , ta.report_cnt , tu.user_nicknm -- 사용자 닉네임 FROM tb_article ta INNER JOIN tb_user tu -- 사용자 테이블 ON ta.user_no = tu.user_no LEFT OUTER JOIN tb_comment tc -- 댓글 테이블 ON ta.article_no = tc.article_no LEFT OUTER JOIN TB_ARTICLE_LIKE tal -- 좋아요 테이블 ON ta.article_no = tal.article_no WHERE ta.article_index_no = '1' -- 게시판 분류
AND ta.article_no NOTNULL AND ta.user_no = #{user_no}
/*내가 댓글 단 게시물*/ AND tc.article_no NOTNULL AND tc.user_no = #{user_no}
/*내가 좋아요한 게시물*/ AND tal.article_no NOTNULL AND tal.user_no = #{user_no}
/*게시물내용 검색*/ AND ta.article_content LIKE CONCAT('%', #{schText}, '%')
ORDER BY ta.article_no DESC LIMIT 10 -- 10개씩 보이기 고정 OFFSET #{offset} -- 구간 조회

/*com.ottt.ottt.dao.CommunityMapper.getArticleTotalCount 호출*/
SELECT COUNT(DISTINCT ta.article_no) 
FROM tb_article ta
INNER JOIN tb_user tu				-- 사용자 테이블
ON ta.user_no = tu.user_no 	
LEFT OUTER JOIN tb_comment tc		-- 댓글 테이블 
ON ta.article_no = tc.article_no 
LEFT OUTER JOIN TB_ARTICLE_LIKE tal	-- 좋아요 테이블
ON ta.article_no = tal.article_no 
WHERE ta.article_index_no = '1' 	-- 게시판 분류

	AND ta.article_no NOTNULL 
	AND ta.user_no = #{user_no}


	/*내가 댓글 단 게시물*/
	AND tc.article_no NOTNULL 
	AND tc.user_no = #{user_no}


	/*내가 좋아요한 게시물*/
	AND tal.article_no NOTNULL 
	AND tal.user_no = #{user_no}


	/*게시물내용 검색*/
	AND ta.article_content LIKE CONCAT('%', #{schText}, '%')

mapper.insert:
/*com.ottt.ottt.dao.CommunityMapper.insert 호출*/
INSERT INTO tb_article (
	article_content
	, article_image
	, article_image_name
	, user_no 
	, article_create_dt
	, article_index_no
)
VALUES(
	#{article_content}
	, #{article_image}
	, #{article_image_name}
	, #{user_no}
	, now()
	,'1'
)

mapper.delete:
/*com.ottt.ottt.dao.CommunityMapper.delete 호출*/
delete from tb_article
where article_no = #{article_no}

mapper.update:
/*com.ottt.ottt.dao.CommunityMapper.update 호출*/

UPDATE 
	tb_article
SET 
	article_content = #{article_content}

		, article_image = #{article_image}
		, article_image_name = #{article_image_name}

	, article_mod_dt = now()
WHERE article_no = #{article_no} 
AND user_no = #{user_no}

mapper.select:
		/*com.ottt.ottt.dao.CommunityMapper.selectLikeCount 호출*/
        SELECT count(*) as count FROM tb_article_like 
		WHERE article_no = #{article_no} 
		AND user_no = #{user_no}

mapper.insert:
		/*com.ottt.ottt.dao.CommunityMapper.insertLike 호출*/
    	INSERT into tb_article_like(
        	user_no 
        	, article_no 
    	)
    	VALUES(
       		#{user_no}
       		, #{article_no}
   		)

mapper.delete:
		/*com.ottt.ottt.dao.CommunityMapper.deleteLike 호출*/
    	DELETE FROM tb_article_like 
    	WHERE user_no = #{user_no} 
    	AND article_no = #{article_no}
